sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
	initialize: function(strDate) {
		// rightnow stores the current time
		this.rightnow = new GlideDateTime().getLocalDate();
	},

	isDatePast: function(strDate){		
		// Create a GlideDateTime object for the When needed date
		var testdate = new GlideDateTime(strDate).getLocalDate();
		// If the testdate is before rightnow, return true, else return false
		if(this.rightnow.compareTo(testdate) == 1) {
			return true;
		}
		else {
			return false;
		}
	},

	isDateToday: function (strDate) {
		// Create a GlideDateTime object for the When needed date
		var testdate = new GlideDateTime(strDate).getLocalDate();

		// If the testdate is before rightnow, return true, else return false	
		if(this.rightnow.compareTo(testdate) == 0) {
			return true;
		}
		else {
			return false;
		}
	},

	type: 'NeedItUtils'
};
